-- ----------------------------
-- Table structure for m_card
-- ----------------------------
DROP TABLE IF EXISTS `m_card`;
CREATE TABLE `m_card` (
  `id` int unsigned NOT NULL AUTO_INCREMENT COMMENT '卡表主键id',
  `category_id` int unsigned NOT NULL COMMENT '关联卡类别主键id',
  `card_name` varchar(255) NOT NULL COMMENT '卡名称',
  `type` tinyint unsigned NOT NULL DEFAULT '0' COMMENT '卡类型(0:装备,1:辅助)',
  `is_all` tinyint unsigned NOT NULL DEFAULT '0' COMMENT '是否触发所有玩家(0:针对单玩家,1:针对所有玩家)',
  `energy` int NOT NULL DEFAULT '0' COMMENT '所需精力',
  `in` int NOT NULL DEFAULT '0' COMMENT '收入属性(指该卡对收入带来的值，通常为被动收入)',
  `out` int NOT NULL DEFAULT '0' COMMENT '支出(通常为每月净现金流的贷款)',
  `assets_num` int NOT NULL DEFAULT '0' COMMENT '资产数(股票为持股数,房产为首付)',
  `assets_value` int NOT NULL DEFAULT '0' COMMENT '资产价值(股票时为股价,房产时为房产总价)',
  `liabilities` int NOT NULL DEFAULT '0' COMMENT '负债(通常为银行指明的贷款数)',
  `is_banker_follow` tinyint unsigned NOT NULL DEFAULT '0' COMMENT '是否需要banker跟进处理',
  `method` varchar(255) NOT NULL COMMENT '方法(后端接口参照此方法名称来定位调用方法处理)',
  `relation_type` tinyint unsigned NOT NULL DEFAULT '0' COMMENT '关联类型(0:无关联,1:关联卡id,以逗号隔开,2:关联对象,卡id为键,value为值)',
  `relation_card` text COMMENT '关联的要触发的卡id(以","号隔开或键值对的json)',
  `is_need_fill` tinyint unsigned NOT NULL DEFAULT '0' COMMENT '抽到卡时是否需要填写',
  `create_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `status` tinyint unsigned NOT NULL DEFAULT '1' COMMENT '状态(0:禁用,1:启用)',
  `class` tinyint unsigned NOT NULL DEFAULT '0' COMMENT '卡的类别(0:无类别,1:房产,2:股票,3:企业投资,4:觉察投资,5:副业,6:理财产品,7:银行贷款,8:相亲卡,9:逆流卡,10:慈善,11:项目,12:资产信托)',
  `describe` text COMMENT '描述',
  `limit` int DEFAULT NULL COMMENT '限制(倍数或最大值)',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=252 DEFAULT CHARSET=utf8mb3;

